  bottom: -1.5rem;
  left: 0;
  transform: translateX(-50%);
  width: 0;
  height: .3rem;
  background-color:rgba(255, 255, 255, 0.5); /* Grey highlight color */
  transition: width,left 1s ease-in-out;
}

.list__item:not(.active):hover::after{
  left: 50%;
  width: 100%;
}


